$query = mysqli_query($link,"SELECT distinct idCredito, idClienteCredito, credito.codigoProducto, sum(cliente.balance) as balance, persona.telefono1, persona.telefono2, descrProducto,  CONCAT( 'Persona:', credito.idCredito, ' ', persona.nombre, cantidadCredito,  ' col.', credito.observacion,  ' cuota.', credito.cuotaCredito) AS descripcion
FROM dia d
INNER JOIN credito ON d.numDia = credito.diaPago
INNER JOIN cliente ON credito.idClienteCredito = cliente.idPersona
INNER JOIN persona ON cliente.idPersona = persona.idPersona
WHERE balance <1 and  anulada='0'
GROUP BY idClienteCredito
order by   persona.nombre");

	while ($row = mysqli_fetch_array($query)) {
		require_once "jesus.php";
		echo "#...". sum($row['telefono1'],$row['telefono2']);		
	}
	exit;
